INSERT INTO `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) VALUES ( '1', '1', '2015-12-03 07:43:56', '2015-12-03 07:43:56', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2015-12-03 07:43:56', '2015-12-03 07:43:56', '', '0', 'http://test-tasks.local/primary/?p=1', '0', 'post', '', '1' );
INSERT INTO `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) VALUES ( '2', '1', '2015-12-03 07:43:56', '2015-12-03 07:43:56', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:

<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>

...or something like this:

<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>

As a new WordPress user, you should go to <a href="http://test-tasks.local/primary/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2015-12-03 07:43:56', '2015-12-03 07:43:56', '', '0', 'http://test-tasks.local/primary/?page_id=2', '0', 'page', '', '0' );
INSERT INTO `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) VALUES ( '3', '1', '2015-12-03 07:44:29', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2015-12-03 07:44:29', '0000-00-00 00:00:00', '', '0', 'http://test-tasks.local/primary/?p=3', '0', 'post', '', '0' );
INSERT INTO `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) VALUES ( '4', '1', '2015-12-03 07:57:13', '2015-12-03 07:57:13', 'На дереве много листьев.
Они шумят, говорят, спорят, дискуссируют между собой.
Но разве знают они, что их питают корни?
Физики ведут себя как листья.
Они шумят, говорят, спорят, дискуссируют между собой.
Но разве знают они корни науки, если строят фундамент Науки на абстрактных идеях.
Фундамент классической механики построен на абстрактном отдельном абсолютном пространстве и абстрактном отдельном абсолютном времени Ньютона.
Фундамент термодинамики построен на абстрактной теории Идеального газа.
Фундамент теории излучения построен на абстрактной теории Черного тела.
Фундамент СТО построен на абстрактной теории отрицательного Четырехмерного пространства.
На этом абстрактном фундаменте физики строят конкретное здание науки и потом удивляются, что есть некоторые парадоксы в природе.
Но не в природе наблюдаются парадоксы.
Что- то не в порядке с логическим мышлением.
Надо бы остановиться, оглянутся назад и пересмотреть абстрактный фундамент науки.
Но все спешат куда-то вперед и, чтобы понять реальность, создают новые абстракции.
Это путь в "дурную бесконечность".
Вот и живем мы в мире абстракций, парадоксов, в мире Джорджа Орвелла.
Как разорвать эту цепь абстракций?', 'Листья и корни, абстракция и сознание.', '', 'publish', 'open', 'open', '', '%d0%bb%d0%b8%d1%81%d1%82%d1%8c%d1%8f-%d0%b8-%d0%ba%d0%be%d1%80%d0%bd%d0%b8-%d0%b0%d0%b1%d1%81%d1%82%d1%80%d0%b0%d0%ba%d1%86%d0%b8%d1%8f-%d0%b8-%d1%81%d0%be%d0%b7%d0%bd%d0%b0%d0%bd%d0%b8%d0%b5-2', '', '', '2015-12-03 07:57:13', '2015-12-03 07:57:13', '', '0', 'http://test-tasks.local/primary/?p=4', '0', 'post', '', '0' );
INSERT INTO `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) VALUES ( '5', '1', '2015-12-03 07:49:27', '2015-12-03 07:49:27', 'На дереве много листьев.
Они шумят, говорят, спорят, дискуссируют между собой.
Но разве знают они, что их питают корни?
Физики ведут себя как листья.
Они шумят, говорят, спорят, дискуссируют между собой.
Но разве знают они корни науки, если строят фундамент Науки на абстрактных идеях.
Фундамент классической механики построен на абстрактном отдельном абсолютном пространстве и абстрактном отдельном абсолютном времени Ньютона.
Фундамент термодинамики построен на абстрактной теории Идеального газа.
Фундамент теории излучения построен на абстрактной теории Черного тела.
Фундамент СТО построен на абстрактной теории отрицательного Четырехмерного пространства.
На этом абстрактном фундаменте физики строят конкретное здание науки и потом удивляются, что есть некоторые парадоксы в природе.
Но не в природе наблюдаются парадоксы.
Что- то не в порядке с логическим мышлением.
Надо бы остановиться, оглянутся назад и пересмотреть абстрактный фундамент науки.
Но все спешат куда-то вперед и, чтобы понять реальность, создают новые абстракции.
Это путь в "дурную бесконечность".
Вот и живем мы в мире абстракций, парадоксов, в мире Джорджа Орвелла.
Как разорвать эту цепь абстракций?', 'Листья и корни, абстракция и сознание.', '', 'publish', 'open', 'open', '', '%d0%bb%d0%b8%d1%81%d1%82%d1%8c%d1%8f-%d0%b8-%d0%ba%d0%be%d1%80%d0%bd%d0%b8-%d0%b0%d0%b1%d1%81%d1%82%d1%80%d0%b0%d0%ba%d1%86%d0%b8%d1%8f-%d0%b8-%d1%81%d0%be%d0%b7%d0%bd%d0%b0%d0%bd%d0%b8%d0%b5', '', '', '2015-12-03 08:27:52', '2015-12-03 08:27:52', '', '0', 'http://test-tasks.local/primary/?p=5', '0', 'post', '', '0' );
